# do the same but for 14 chars rather than 4
require 'pry'

stream_input = ""

#have a l and a r pointer 2 apart
# if all chars between and including the l and r pointer are unique
# return r + 1
# else increment pointers by 1
# 

def find_stream_start(stream)
  l = 0
  r = 13

  loop do
    sub_string = (l..r).map {|i| stream[i] }

    break if sub_string == sub_string.uniq
    l += 1
    r += 1
  end
  r + 1
end


puts find_stream_start(stream_input)
